import React, { useState } from 'react';
import fakeData from '../../fakeData';
import { addToDatabaseCart} from '../../utilities/fakedb';
import Cart from '../Cart/Cart';
import Products from '../Products/Products';
import './Shop.css';

const Shop = () => {
    const sliceFakeData = fakeData.slice(0, 10);
    const [products, setProducts] = useState(sliceFakeData);

    const [cart, setCart] = useState([])

            const handleAddToCart = (product) => { 

                const newCart = [...cart, product];
                    setCart(newCart)

                    const sameProduct = newCart.filter(pd => pd.key === product.key);
                    const count = sameProduct.length;
                    addToDatabaseCart(product.key, count);
            }


    return (
        <div className='shop-container'>
            <div className='products'>
            {
                products.map(product => <Products showAddToCart={true} handleAddToCart = {handleAddToCart} products = {product}></Products>)
            }
        </div>

        <div className='cart'>
            <Cart cart= {cart}></Cart>
        </div>
        </div>
    );
};

export default Shop;
